mapper.update:
update BOARD
set board_count = board_count + 1
where board_no = #{bNo}

update BOARD
set board_title = #{boardTitle}, board_content=#{boardContent}

, board_cate=#{boardCate}


, image_url=#{imageUrl}


, news_url=#{newsURL}

where board_No= #{boardNo}

update BOARD
set board_status = 'N'
where board_no = #{bId}

mapper.select:
select * from BOARD join USER using(u_no) WHERE board_title like '%' || #{keyword} || '%' and board_type = #{i} and board_status='Y'
and board_cate = #{category}
order by board_no desc

mapper.update:
update REPLY
set re_status = 'N'
where re_no = #{replyNo}

update REPLY
set re_status = 'N'
where board_no = #{boardNo}

update REPLY
set re_content = #{replyContent}
where re_no = #{replyNo}

mapper.select:
select *
from REPLY
where re_no = #{replyNo}

mapper.delete:
update REPLY
set re_status = 'N'
where re_no = #{replyNo}
